from flask import (g)def findById(user_id, id):    cur = g.db.execute("SELECT * FROM todos WHERE id ='%s' AND user_id='%s'" % (id, user_id))    return cur.fetchone()def countAll(user_id):    cur = g.db.execute("SELECT COUNT(id) as elementTotal FROM todos WHERE user_id = '%s'" % user_id)    return cur.fetchone()["elementTotal"]def findLimited(user_id, nb, offset):    cur = g.db.execute("SELECT * FROM todos WHERE user_id = '%s' LIMIT %d, %d" % (user_id, offset, nb))    return cur.fetchall()def add(user_id, description):    if description:        g.db.execute(            "INSERT INTO todos (user_id, description) VALUES ('%s', '%s')"            % (user_id, description)        )        g.db.commit()        return True    return Falsedef delete(user_id, id):    g.db.execute("DELETE FROM todos WHERE id ='%s' AND user_id='%s'" % (id, user_id))    g.db.commit()def updateStatus(user_id, id, status):    g.db.execute("UPDATE todos SET status = %d WHERE id = '%s' AND user_id = '%s'" % (status, id, user_id))    g.db.commit()